import { useEffect, useState } from "react";
import StarRating from "./starRating";

const average = (arr) =>
  arr.reduce((acc, cur, i, arr) => acc + cur / arr.length, 0);

const key = "91899472";

export default function App() {
  // const [watched, setWatched] = useState([]);
  const [movies, setMovies] = useState([]);
  const [loader, setLoader] = useState(false);
  const [error, setError] = useState("");
  const [query, setQuery] = useState("");
  const [selectedId, setSelectedId] = useState("");

  const [watched, setWatched] = useState(function () {
    const storedValue = localStorage.getItem("watched");
    return storedValue ? JSON.parse(storedValue) : [];
  });

  function addSetWatched(mov) {
    setWatched((watched) => [...watched, mov]);
  }

  useEffect(
    function () {
      localStorage.setItem("watched", JSON.stringify(watched));
    },
    [watched]
  );

  function handleSelectId(id) {
    setSelectedId((selectedId) => (selectedId === id ? null : id));
  }
  function handleCloseDetail() {
    setSelectedId(null);
  }

  function handleDeleteWatched(id) {
    setWatched((w) => w.filter((m) => m.imdbID !== id));
  }

  useEffect(
    function () {
      const controller = new AbortController();

      async function movieList() {
        try {
          setError("");
          setLoader(true);
          const movieFetch = await fetch(
            `http://www.omdbapi.com/?apikey=${key}&s=${query}`,
            { signal: controller.signal }
          );
          if (!movieFetch.ok) {
            throw new Error("Error on loading");
          }
          const json = await movieFetch.json();
          if (json.Response === "False") throw new Error("Movie not found");

          setMovies(json.Search);
          // console.log(json.Search);
          setError("");
        } catch (err) {
          if (!err.name !== "AbortError") {
            setError(err.message);
          }
        } finally {
          setLoader(false);
        }
      }
      if (!query.length) {
        setMovies([]);
        setError("Search something great to watch!");
        return;
      }
      handleCloseDetail();
      movieList();

      return function () {
        controller.abort();
      };
    },
    [query]
  );

  return (
    <>
      <Nav movies={movies}>
        <Logo />
        <Search query={query} setQuery={setQuery} />
        <Numresults movies={movies} />
      </Nav>
      <Main>
        <Box>
          {loader && <Loader />}
          {!loader && !error && (
            <MovieList onSelectId={handleSelectId} movies={movies} />
          )}
          {error && <ErrorShow message={error} />}
          {/* {loader ? <Loader /> : <MovieList movies={movies} />} */}
        </Box>
        <Box>
          {selectedId ? (
            <MovieDetails
              details={selectedId}
              onCloseDetail={handleCloseDetail}
              onSetWatched={addSetWatched}
              watched={watched}
            />
          ) : (
            <>
              <WatchedSummary watched={watched} />
              <WatchMoviesList
                watched={watched}
                handleDeleteWatched={handleDeleteWatched}
              />
            </>
          )}
        </Box>
      </Main>
    </>
  );
}

function Loader() {
  return <p className="loader">Loading..</p>;
}
function ErrorShow({ message }) {
  return <p className="error">{message}</p>;
}

function Nav({ children }) {
  return <nav className="nav-bar">{children}</nav>;
}

function Logo() {
  return (
    <div className="logo">
      <span role="img">🍿</span>
      <h1>usePopcorn</h1>
    </div>
  );
}
function Numresults({ movies }) {
  return (
    <p className="num-results">
      Found <strong>{movies.length}</strong> results
    </p>
  );
}

function MovieDetails({ details, onCloseDetail, onSetWatched, watched }) {
  const [movie, setMovie] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [userRating, setUserRating] = useState("");

  const thisRating = watched.find((m) => m.imdbID === details)?.userRating;
  const isWatched = watched.map((movie) => movie.imdbID).includes(details);

  const {
    imdbID,
    Title: title,
    Year: year,
    Poster: poster,
    Runtime: runtime,
    imdbRating,
    Plot: plot,
    Released: released,
    Actors: actors,
    Director: director,
    Genre: genre,
  } = movie;

  function handleAdd() {
    const addMovie = {
      imdbID,
      title,
      year,
      poster,
      runtime: Number(runtime.split(" ").at(0)),
      released,
      imdbRating: Number(imdbRating),
      userRating,
    };
    onSetWatched(addMovie);
    onCloseDetail();
  }

  useEffect(
    function () {
      async function movieDetails() {
        setIsLoading(true);
        const movieFetch = await fetch(
          `http://www.omdbapi.com/?apikey=${key}&i=${details}`
        );
        const data = await movieFetch.json();
        setMovie(data);
        setIsLoading(false);
      }
      movieDetails();
    },
    [details]
  );

  useEffect(
    function () {
      document.title = `Movie | ${title ? title : "..."}`;
      return function () {
        document.title = "Pop Corn";
      };
    },
    [title]
  );

  return (
    <div className="details">
      {isLoading ? (
        <Loader />
      ) : (
        <>
          <header>
            <button className="btn-back" onClick={onCloseDetail}>
              &larr;
            </button>
            <img src={poster} alt={`Poster of ${movie} movie`} />
            <div className="details-overview">
              <h2>{title}</h2>
              <p>
                {released} &bull; {runtime}
              </p>
              <p>{genre}</p>
              <p>{imdbRating} IMDb rating</p>
            </div>
          </header>
          <section>
            <div className="rating">
              {!isWatched ? (
                <>
                  <StarRating
                    size={25}
                    maxRating={10}
                    setOutRating={(e) => setUserRating(e)}
                  />

                  {userRating > 0 && (
                    <button onClick={handleAdd} className="btn-add">
                      + Add Movie list
                    </button>
                  )}
                </>
              ) : (
                <p>You have already rated this movie Rating:- {thisRating}</p>
              )}
            </div>

            <p>
              <em>{plot}</em>
            </p>
            <p>Starring {actors}</p>
            <p>Directed by {director}</p>
          </section>
        </>
      )}
    </div>
  );
}

function Search({ query, setQuery }) {
  return (
    <input
      className="search"
      type="text"
      placeholder="Search movies..."
      value={query}
      onChange={(e) => setQuery(e.target.value)}
    />
  );
}

function Main({ children }) {
  return <main className="main">{children}</main>;
}

function Box({ children }) {
  const [isOpen, setIsOpen] = useState(true);

  return (
    <div className="box">
      <button className="btn-toggle" onClick={() => setIsOpen((open) => !open)}>
        {isOpen ? "–" : "+"}
      </button>

      {isOpen && children}
    </div>
  );
}

function MovieList({ movies, onSelectId }) {
  return (
    <ul className="list list-movies">
      {movies?.map((movie) => (
        <Movie movie={movie} key={movie.imdbID} onSelectId={onSelectId} />
      ))}
    </ul>
  );
}

function Movie({ movie, onSelectId }) {
  return (
    <li onClick={() => onSelectId(movie.imdbID)} key={movie.imdbID}>
      <img src={movie.Poster} alt={`${movie.Title} poster`} />
      <h3>{movie.Title}</h3>
      <div>
        <p>
          <span>🗓</span>
          <span>{movie.Year}</span>
        </p>
      </div>
    </li>
  );
}

function WatchedSummary({ watched }) {
  const avgImdbRating = average(watched.map((movie) => movie.imdbRating));
  const avgUserRating = average(watched.map((movie) => movie.userRating));
  const avgRuntime = average(watched.map((movie) => movie.runtime));
  return (
    <div className="summary">
      <h2>Movies you watched</h2>
      <div>
        <p>
          <span>#️⃣</span>
          <span>{watched.length} movies</span>
        </p>
        <p>
          <span>⭐️</span>
          <span>{avgImdbRating.toFixed(2)}</span>
        </p>
        <p>
          <span>🌟</span>
          <span>{avgUserRating.toFixed(2)}</span>
        </p>
        <p>
          <span>⏳</span>
          <span>{avgRuntime} min</span>
        </p>
      </div>
    </div>
  );
}

function WatchMoviesList({ watched, handleDeleteWatched }) {
  return (
    <ul className="list">
      {watched.map((movie) => (
        <WatchedMovie
          handleDeleteWatched={handleDeleteWatched}
          movie={movie}
          key={movie.imdbID}
        />
      ))}
    </ul>
  );
}

function WatchedMovie({ movie, handleDeleteWatched }) {
  return (
    <li key={movie.imdbID}>
      <img src={movie.poster} alt={`${movie.title} poster`} />
      <h3>{movie.Title}</h3>
      <div>
        <p>
          <span>⭐️</span>
          <span>{movie.imdbRating}</span>
        </p>
        <p>
          <span>🌟</span>
          <span>{movie.userRating}</span>
        </p>
        <p>
          <span>⏳</span>
          <span>{movie.runtime}</span>
        </p>
        <button
          className="btn-delete"
          onClick={() => handleDeleteWatched(movie.imdbID)}
        >
          X
        </button>
      </div>
    </li>
  );
}
